<ul class="table-forms" id="permissionsFormList"
            style="margin: 0; padding: 0; border: 0; font-size: 100%; font: inherit; vertical-align: baseline; list-style: none; display: table; width: 100%; color: #1f2c33;">
            <li
                style="margin: 0; padding: 0; border: 0; font-size: 100%; font: inherit; vertical-align: baseline; display: table-row;">
                <div class="table-forms-td-left"
                    style="margin: 0; border: 0; font-size: 100%; font: inherit; display: table-cell; padding: 5px 0; text-align: right; vertical-align: top; width: 15%; white-space: nowrap;">
                    <label class="form-label-asterisk"
                        style="margin: 0; padding: 0; border: 0; font-size: 100%; font: inherit; vertical-align: baseline; display: block; height: 24px; line-height: 24px;">Role</label>
                </div>
                <div class="table-forms-td-right"
                    style="margin: 0; border: 0; font-size: 100%; font: inherit; display: table-cell; padding: 5px 0 5px 10px; vertical-align: middle; width: 85%; position: relative;">
                    <div class="multiselect-control"
                        style="margin: 0; padding: 0; border: 0; font-size: 100%; font: inherit; display: flex; position: relative; vertical-align: top; white-space: nowrap;">
                        <div id="roleid" class="multiselect" role="application"
                            style="margin: 0; padding: 0; font-size: 100%; font: inherit; vertical-align: baseline; display: flex; flex-direction: column; position: relative; margin-right: 3px; min-height: 24px; white-space: normal; background-color: #ffffff; border: 1px solid #acbbc2; box-sizing: border-box; color: #1f2c33; outline: 0; transition: border-color .2s ease-out, box-shadow .2s ease-out; width: 270px;">
                            <div aria-live="assertive" aria-atomic="true"
                                style="margin: 0; padding: 0; border: 0; font-size: 100%; font: inherit; vertical-align: baseline; overflow: hidden; position: absolute; left: 0; right: 0; top: 0; bottom: 0; z-index: -1;">
                            </div>
                            <div class="selected"
                                style="margin: 0; padding: 0; border: 0; font-size: 100%; font: inherit; vertical-align: baseline;">
                                <ul class="multiselect-list"
                                    style="margin: 0; padding: 0; border: 0; font-size: 100%; font: inherit; vertical-align: baseline; list-style: none; display: flex; flex-direction: row; flex-wrap: wrap; padding-left: 2px; padding-bottom: 2px;">
                                </ul>
                            </div>
                            <input class="input" type="text" autocomplete="off" placeholder="type here to search"
                                aria-label="type here to search" aria-required="false"
                                style="font-family: Arial, Tahoma, Verdana, sans-serif; font-size: 1em; margin: 0; padding: 0 5px; background-color: #ffffff; color: #1f2c33; outline: 0; transition: border-color .2s ease-out, box-shadow .2s ease-out; border: 0; background: none; box-sizing: border-box; width: 100%; padding-bottom: 2px; min-height: 20px;">
                        </div>
                        <div class="multiselect-button"
                            style="margin: 0; padding: 0; border: 0; font-size: 100%; font: inherit; vertical-align: top;">
                            <button type="button" class="btn-grey"
                                style="font-family: Arial, Tahoma, Verdana, sans-serif; font-size: 1em; border: 1px solid #0275b8; border-radius: 2px; margin: 0; padding: 0 11px; min-height: 24px; cursor: pointer; outline: 0; transition: border-color .2s ease-out, background-color .2s ease-out; color: #1f2c33; background-color: #ffffff; border-color: #acbbc2; vertical-align: top;">Select</button>
                        </div>
                    </div>
                </div>
            </li>
        </ul>
